import React, { useState, useEffect } from "react";
import {
  StyleSheet,
  Text,
  View,
  SafeAreaView,
  ActivityIndicator,
  FlatList,
} from "react-native";

// get data from this URL!
const petsURL = "http://localhost/cadastrapet/app/services/api_teste.php";

const App = () => {
  // managing state with 'useState'
  const [isLoading, setLoading] = useState(true);
  const [data, setData] = useState([]);

  //editar
  const [nome, setNome] = useState([]);
  const [raca, setRaca] = useState([]);

  // similar to 'componentDidMount', gets called once
  useEffect(() => {
    fetch(petsURL)
      .then((response) => response.json()) // get response, convert to json
      .then((json) => {
        setData(json.pets);
        setNome(json.nome);
        setRaca(json.raca);
      })
      .catch((error) => alert(error)) // display errors
      .finally(() => setLoading(false)); // change loading state
  }, []);

  // Also get call asynchronous function
  async function getPetsAsync() {
    try {
      let response = await fetch(petsURL);
      let json = await response.json();
      setData(json.pets);
      setNome(json.nome);
      setRaca(json.raca);
      setLoading(false);
    } catch (error) {
      alert(error);
    }
  }

  return (
    <SafeAreaView style={styles.container}>
      {/* While fetching show the indicator, else show response*/}
      {isLoading ? (
        <ActivityIndicator />
      ) : (
        <View>
          {/* Title from URL */}
          <Text style={styles.title}>{title}</Text>
          {/* Display each movie */}
          <View style={{ borderBottomWidth: 1, marginBottom: 12 }}></View>
          <FlatList
            data={data}
            keyExtractor={({ id }, index) => id}
            renderItem={({ item }) => (
              <View style={{ paddingBottom: 10 }}>
                <Text style={styles.movieText}>
                  {item.id}. {item.nome}, 
                </Text>
              </View>
            )}
          />
          {/* Show the description */}
          <Text style={styles.title}>{item.raca}</Text>
        </View>
      )}
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: "center",
    marginTop: 48,
  },
  movieText: {
    fontSize: 26,
    fontWeight: "200",
  },
  title: {
    fontSize: 32,
    fontWeight: "bold",
  },
  description: {
    textAlign: "center",
    marginBottom: 18,
    fontWeight: "200",
    color: "green",
  },
});

export default App;
